import React, { useContext, useEffect, useState } from "react";
import "./CartItems.css";
import { ShopContext } from "../../Context/ShopContext";
import remove_icon from "../Assets/cart_cross_icon.png";
import axios from "axios";

const CartItems = () => {
  const {
    products,          // changed from all_product to products (same as ShopContext export)
    cartItems,
    setCartItems,
    getTotalCartAmount,
  } = useContext(ShopContext);

  const [promoCode, setPromoCode] = useState("");
  const [discountPercent, setDiscountPercent] = useState(0);
  const [promoMessage, setPromoMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [token, setToken] = useState(null);

  const totalAmount = getTotalCartAmount();
  const discountAmount = (totalAmount * discountPercent) / 100;
  const finalAmount = totalAmount - discountAmount;

  // Load token from localStorage on mount
  useEffect(() => {
    const storedToken = localStorage.getItem("auth-token");  // <-- Changed here
    if (storedToken) {
      setToken(storedToken);
    }
    setLoading(false);
  }, []);

  // Fetch cart when token is available
  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await axios.post(
          "http://localhost:4001/getcart",
          {},
          {
            headers: { "auth-token": token },
          }
        );
        setCartItems(res.data || {});
      } catch (err) {
        console.error("Failed to fetch cart data:", err);
      }
    };

    if (token) {
      fetchCart();
    }
  }, [token, setCartItems]);

  // Remove item from cart handler
  const handleRemoveFromCart = async (itemId) => {
    try {
      const res = await axios.post(
        "http://localhost:4001/removefromcart",
        { itemId },
        { headers: { "auth-token": token } }
      );
      setCartItems(res.data.cartData);
    } catch (err) {
      console.error("Error removing item from cart:", err);
    }
  };

  const applyPromoCode = async () => {
    if (!promoCode.trim()) {
      setPromoMessage("Please enter a promo code.");
      return;
    }

    try {
      const res = await axios.post("http://localhost:4001/validate-promo", {
        promoCode: promoCode.trim(),
      });

      if (res.data.success) {
        setDiscountPercent(res.data.discount);
        setPromoMessage(`Promo code applied! ${res.data.discount}% discount.`);
      } else {
        setDiscountPercent(0);
        setPromoMessage("Invalid promo code.");
      }
    } catch (err) {
      setDiscountPercent(0);
      setPromoMessage("Server error or invalid code.");
    }
  };

  const proceedToCheckout = () => {
    if (finalAmount <= 0) {
      alert("Cart is empty. Add some items first!");
      return;
    }

    alert(
      `Proceeding to checkout\nSubtotal: â‚¹${totalAmount}\nDiscount: â‚¹${discountAmount.toFixed(
        2
      )}\nTotal to pay: â‚¹${finalAmount.toFixed(2)}`
    );
  };

  const isCartEmpty =
    !cartItems ||
    Object.values(cartItems).reduce((sum, qty) => sum + qty, 0) === 0;

  if (loading) {
    return (
      <div className="cartItems">
        <p style={{ padding: "30px", textAlign: "center" }}>Loading cart...</p>
      </div>
    );
  }

  if (!token) {
    return (
      <div className="cartItems">
        <p style={{ padding: "30px", textAlign: "center" }}>
          ðŸ”’ Please <strong>log in</strong> to view your cart.
        </p>
      </div>
    );
  }

  return (
    <div className="cartItems">
      <div className="cartItems-scroll">
        <div className="cartItems-format-main">
          <p>Products</p>
          <p>Title</p>
          <p>Price</p>
          <p>Quantity</p>
          <p>Total</p>
          <p>Remove</p>
        </div>
        <hr />

        {Array.isArray(products) && products.length > 0 ? (
          products.map((e) => {
            if (cartItems[e.id] > 0) {
              return (
                <div key={e.id}>
                  <div className="cartItems-format cartItems-format-main cart-item-row">
                    <img
                      src={e.image}
                      alt="product"
                      className="cartItem-product-icon"
                    />
                    <p>{e.name}</p>
                    <p>â‚¹ {e.new_price}</p>
                    <button className="cartItem-quantity">{cartItems[e.id]}</button>
                    <p>â‚¹ {e.new_price * cartItems[e.id]}</p>
                    <img
                      className="cartItem-remove-icon"
                      src={remove_icon}
                      alt="remove"
                      onClick={() => handleRemoveFromCart(e.id)}
                    />
                  </div>
                  <hr />
                </div>
              );
            }
            return null;
          })
        ) : (
          <p style={{ textAlign: "center", padding: "20px" }}>Your cart is empty.</p>
        )}
      </div>

      <div className="cartItems-down">
        <div className="cartItems-total">
          <h1>Cart Total</h1>
          <div>
            <div className="cartItems-total-item">
              <p>Subtotal</p>
              <p>â‚¹{totalAmount.toFixed(2)}</p>
            </div>
            <hr />
            <div className="cartItems-total-item">
              <p>Discount</p>
              <p>â‚¹{discountAmount.toFixed(2)}</p>
            </div>
            <hr />
            <div className="cartItems-total-item">
              <p>Shipping Fee</p>
              <p>Free</p>
            </div>
            <hr />
            <div className="cartItems-total-item">
              <h3>Total</h3>
              <h3>â‚¹{finalAmount.toFixed(2)}</h3>
            </div>
          </div>
          <button onClick={proceedToCheckout} disabled={isCartEmpty}>
            PROCEED TO CHECKOUT
          </button>
        </div>

        <div className="cartItems-promocode">
          <p>If you have a promo code, enter it here:</p>
          <div className="cartItems-promobox">
            <input
              type="text"
              placeholder="Promo code"
              value={promoCode}
              onChange={(e) => setPromoCode(e.target.value)}
            />
            <button onClick={applyPromoCode}>Apply</button>
          </div>
          {promoMessage && <p>{promoMessage}</p>}
        </div>
      </div>
    </div>
  );
};

export default CartItems;
